import React, {Component} from 'react';

import Navigation from './components/Navigation/Navigation';

import FaceRecognition from './components/FaceRecognition/FaceRecognition';

import Logo from './components/Logo/Logo';

import Clarifai from "clarifai";

import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';

import Rank from './components/Rank/Rank';

import Diagnosis from './components/Diagnosis/Diagnosis';

import Particles from 'react-particles-js';

//import FaceDetect from "./components/FaceDetect/FaceDetect";


import './App.css';


 // You need to add your own API key here from Clarifai.
const app = new Clarifai.App({
  apiKey: "e94b16ebf46741309ecb3eeef61e76a3",
});



const particlesOptions =  {



                particles: {
                  number: {
                    value: 70,
                      density: {
                      enable: true,
                     value_area: 800
                    }
                  }
                }


 }






class App extends Component {

constructor(){
  super() // to use this

  this.state = {
    input: '',
     imageUrl: '',
     box:{}
  }

}

  calculateFaceLocation = (data) => {
     console.log(data);
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    }
  }


  displayFaceBox = (box) => {
    console.log("box function");
    this.setState({box: box});
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value});
console.log(event.target.value);

  }

  onButtonSubmit = () => {

      // set imageUrl state
    this.setState({ imageUrl: this.state.input });
    app.models
          .predict(
            Clarifai.FACE_DETECT_MODEL, this.state.input).then(
      function (response) {
       console.log(response);
      this.calculateFaceLocation(response)
    //this.displayFaceBox(this.calculateFaceLocation(response));
        // response data fetch from FACE_DETECT_MODEL 

      //  this.calculateFaceLocation(response)
      //  console.log(response);
        /* data needed from the response data from clarifai API, 
           note we are just comparing the two for better understanding 
           would to delete the above console*/ 
        console.log(
          response.outputs[0].data.regions[0].region_info.bounding_box
        );
      },
      function (err) {
         console.log(err);
        // there was an error
      }
    );
  }

  render(){

 return (
    <div className="App">


           <Particles className = 'particles' params={particlesOptions}/>




     <Navigation/>
     <Logo />
      <Diagnosis/>
       <Rank/>
     <ImageLinkForm 
     onInputChange={this.onInputChange} 
     onButtonSubmit={this.onButtonSubmit}/>

     <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>


    </div>
  );



  }



}

export default App;
