import React, {Component,useSate} from 'react';

import Navigation from './components/Navigation/Navigation';

import FaceRecognition from './components/FaceRecognition/FaceRecognition';

import Logo from './components/Logo/Logo';

import Clarifai from "clarifai";

import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';

import Rank from './components/Rank/Rank';

import Diagnosis from './components/Diagnosis/Diagnosis';

import Particles from 'react-particles-js';

//import FaceDetect from "./components/FaceDetect/FaceDetect";




import './App.css';


const particlesOptions =  {



                particles: {
                  number: {
                    value: 70,
                      density: {
                      enable: true,
                     value_area: 800
                    }
                  }
                }


 }


 // You need to add your own API key here from Clarifai.
const app = new Clarifai.App({
  apiKey: "e94b16ebf46741309ecb3eeef61e76a3",
});





class App extends Component {



constructor(){
  super() // to use this
	this.handleSubmit = this.handleSubmit.bind(this);
		this.handleInputChange = this.handleInputChange.bind(this);

  this.state = {
    input: '',
     imageUrl: '',
    box: {},
     fileName: ''
  }

}

  handleSubmit (evt) {
		evt.preventDefault();
  }

  handleInputChange (evt) {
		this.setState({
			fileName: this.fileInput.value
				? this.fileInput.files[0].name
				: ''
		});
	}

  calculateFaceLocation = (data) => {
    console.log("inside face Location");
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    }
  }

displayFaceBox = (box) => {
  console.log(box);
  this.setState({box: box});
}

  onInputChange = (event) => {
    this.setState({ input: event.target.value});
//console.log(event.target.value);

  }

  onButtonSubmit = () => {

    var formdata = new FormData();
    var fileInput = this.state.fileName;

formdata.append("data", fileInput, "acne.jpg");

var requestOptions = {
  method: 'POST',
  body: formdata,
  redirect: 'follow'
};

fetch("https://www.nyckel.com/v0.9/functions/edx2ml1gbri4n34d/invoke", requestOptions)
  .then(response => response.text())
  .then(result => console.log(result))
  .catch(error => console.log('error', error));


    app.models
      .predict(Clarifai.FACE_DETECT_MODEL, this.state.input)
      .then((response) =>
        // calculateFaceLocation function pass to displaybox as is parameter
        this.displayFaceBox(this.calculateFaceLocation(response))
      )
      // if error exist console.log error
      .catch((err) => console.log(err));
  };


  render() {


	document.querySelector('#root')
    return (



       <div className="App">


<form onSubmit={this.handleSubmit}>
				<label>
					<input type="file"
						onChange={this.handleInputChange}
						ref={input => this.fileInput = input}
						className="form-control" />
				</label>
				<br />
				{
					this.state.fileName
						&& <h4 className="mt-3">
								File: <span className="text-danger">{this.state.fileName}</span>
            </h4>


				}
			</form>





           <Particles className = 'particles' params={particlesOptions}/>




     <Navigation/>
     <Logo />
      <Diagnosis/>
       <Rank/>
     <ImageLinkForm 
     onInputChange={this.onInputChange} 
     onButtonSubmit={this.onButtonSubmit}/>

     <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>


    </div>
  );



  }




}




export default App;
